    async public void SafeTransferFrom()
    {
        if(!WebLogin2.inicio_test){
            toAccount =PlayerPrefs.GetString("Account");
            string method = "mintNFT";
            string[] obj = { "https://ipfs.io/ipfs/"+PlayerPrefs.GetString("json") };
            string args = JsonConvert.SerializeObject(obj);
            string value = "0";
            // gas limit OPTIONAL
            string gasLimit = "20000000";
            // gas price OPTIONAL
            string gasPrice = "250000";
            print(contract);
            // connects to user's browser wallet (metamask) to send a transaction
            try {
                string response = await Web3GL.SendContract(method, abi1, contract, args, value, gasLimit, gasPrice);
                Debug.Log(response);
                SceneManager.LoadScene("Demo_Fighter2D");
                
                bancodedados.salvarint("selecionar",bancodedados.carregarint("totalPerso"));
                salvar_persoatual();

            } catch (Exception e) {
                Debug.LogException(e, this);
            }
        }
        else{
            SceneManager.LoadScene("Demo_Fighter2D");
            bancodedados.salvarint("selecionar",bancodedados.carregarint("totalPerso"));
            salvar_persoatual();
        }
    }

